const getBlogPost = async (id) => {
  try {
    const response = await fetch("http://127.0.0.1:5000/getBlogPost?id=${id}", {
      method: "GET",
    });

    if (!response.ok) {
      throw new Error("Could't download the blog post");
    }

    const data = await response.json();

    console.log(data);
    return data;
  } catch (error) {
    console.error(e);
  }
};

export default getBlogPost;
