import { onMounted, ref } from "vue";
export default {
  __name: 'Opinions',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const opinions = ref([{
      author: "Anna Kowalska",
      review: "Pan Rafał Masłowski pomógł mi uzyskać kredyt na mieszkanie w szybki i bezproblemowy sposób. Jego wiedza i profesjonalizm zrobiły na mnie duże wrażenie."
    }, {
      author: "Piotr Nowak",
      review: "Dzięki panu Rafałowi udało mi się wybrać najkorzystniejszą ofertę kredytową. Jestem bardzo zadowolony z jego indywidualnego podejścia i cierpliwości."
    }, {
      author: "Magdalena Wiśniewska",
      review: "Pan Rafał to doradca, który naprawdę dba o klienta. Jego porady były konkretne i rzeczowe, a cały proces przebiegł szybko i bez komplikacji."
    }]);
    const showElement = ref([false, false, false, false]);
    onMounted(() => {
      const observer = new IntersectionObserver(entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            for (let i = 0; i < 4; i++) {
              setTimeout(() => {
                showElement.value[i] = true;
              }, i * 150);
            }
          }
        });
      });
      const target = document.getElementById(`opinion-element-observe-target`);
      observer.observe(target);
    });
    const __returned__ = {
      opinions,
      showElement,
      onMounted,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
