const getImg = async (url) => {
  try {
    const response = await fetch(url, {
      method: "GET",
    });

    if (!response.ok) {
      throw new Error("could't fetch the image");
    }

    const data = await response.blob();
    return URL.createObjectURL(data);
  } catch (error) {
    console.error(error);
  }
};

export default getImg;
