import { defineStore } from "pinia";
import getBlogPosts from "@/composables/getBlogPosts";

export const usePosts = defineStore("posts", {
  state: () => {
    return { isLoading: true, posts: [] };
  },
  actions: {
    // since we rely on `this`, we cannot use an arrow function
    getPosts() {
      this.count++;
    },
  },
});
