export const activateBlogPost = async (postID) => {
  try {
    const response = await fetch(
      `http://127.0.0.1:5000/activateBlogPost?id=${blogPost.value.id}`,
      {
        method: PUT,
      }
    );
    if (!response.ok) {
      throw new Error("could't change the post");
    }
    const data = await response.json();
  } catch (error) {
    console.error(error);
  }
};
